-- --------------------------------------------------------------------------------------
-- CH-47F - scripted controls start here ------------------------------------------------
-- --------------------------------------------------------------------------------------
local function pdp_command ( pdp_, row_, column_, name_, name_off_, name_on_ )
    local row = string.byte(row_) - 65
    local displacement = (row * 33) + column_ - 1

    local device_ = -1
    local command_ = device_commands.Button_1 + displacement
    local category_ = _("General")
    if pdp_==1 then
        device_ = devices.PDP1
        category_ = {_("Power Distribution Panel 1"), _("Electrical System"),}
    elseif pdp_==2 then
        device_ = devices.PDP2
        category_ = {_("Power Distribution Panel 2"), _("Electrical System"),}
    end

    return {
        {cockpit_device_id = device_, down = command_, value_down = 0.5, up = command_, value_up = 0.0, name = name_,     category = category_},
        {cockpit_device_id = device_, down = command_, value_down = 0.9, up = command_, value_up = 0.0, name = name_off_, category = category_},
        {cockpit_device_id = device_, down = command_, value_down = 0.6, up = command_, value_up = 0.0, name = name_on_,  category = category_},
    }
end



